```class NH_DOSIMETER_2 extends NH_DOSIMETER_1
{
    static float                 START_ANIM_VALUE = 0.830;
    static float                 END_ANIM_VALUE = 7.965;

    float                         m_CurrAnimation = START_ANIM_VALUE;

    bool                         m_InLightMode;

    static float                 m_LightMode = 2.259887005649718;
    static float                 m_HardMode = 12.5549278091651;

    override void OnWork( float consumed_energy ) 
    {
        int multiplier = 1;

        if (GetGame().IsClient())
        {            
            if(m_DisplayedStrength > 0)
            {
                if(m_DisplayedStrength > m_RadStrength)
                    multiplier = Math.Round(m_DisplayedStrength / m_RadStrength);
                else
                    multiplier = Math.Round(m_RadStrength / m_DisplayedStrength);

                if(multiplier < 1) multiplier = 1;    
            }


            if(m_DisplayedStrength < m_RadStrength)
            {
                m_DisplayedStrength += Math.RandomInt(1, 5) * multiplier;
            }
            else
            {
                m_DisplayedStrength -= Math.RandomInt(1, 5) * multiplier;
            }
            if(m_CurrAnimation > 7.8 && m_InLightMode)
            { 
                SwitchMode();
            }
            else if(m_CurrAnimation < 0.850 && !m_InLightMode)
            {    
                SwitchMode();
            }

            float target_anim_value = m_RadStrength / GetMode();

            target_anim_value = Math.Clamp(target_anim_value * 0.1, START_ANIM_VALUE, END_ANIM_VALUE);

            if(m_CurrAnimation > target_anim_value)
            {
                while(m_CurrAnimation > target_anim_value)
                {
                    RemoveAnimValue(0.1);
                }

            } else
                {
                    while(m_CurrAnimation < target_anim_value)
                    {
                        AddAnimValue(0.1);
                    }
                }

            float koef = (m_RadStrength/100) / 2;

            m_Time += 0.024 * koef;

            if(m_Time >= 0.2)
            {
                m_GeigerSound = SEffectManager.PlaySoundOnObject( "NH_Geiger_SoundSet", this, 0, 0, false );
                m_GeigerSound.SetSoundAutodestroy( true );
                m_Time = 0.0;
            }
        }
        else if(GetGame().IsServer())
        {
            super.OnWork( consumed_energy );
        }
    }

    void SwitchMode()
    {
        m_InLightMode = !m_InLightMode;
        ResetAnimValue();
    }

    float GetMode()
    {
        if(m_InLightMode)
            return m_LightMode;

        return m_HardMode;
    }

    void ResetAnimValue()
    {
        m_CurrAnimation = START_ANIM_VALUE;
        SetAnimationPhase("strelka", m_CurrAnimation);
    }    

    void RemoveAnimValue(float val)
    {
        m_CurrAnimation -= val;
        m_CurrAnimation = Math.Clamp(m_CurrAnimation, START_ANIM_VALUE, END_ANIM_VALUE);
        SetAnimationPhase("strelka", m_CurrAnimation);
    }

    void AddAnimValue(float val)
    {
        m_CurrAnimation += val;
        m_CurrAnimation = Math.Clamp(m_CurrAnimation, START_ANIM_VALUE, END_ANIM_VALUE);
        SetAnimationPhase("strelka", m_CurrAnimation);
    }

    override void OnWorkStop()
    {    
        if(GetGame().IsClient())
        {
            m_InLightMode = true;
            ResetAnimValue();
        }
    }

    override void OnWorkStart()
    {    
        if(GetGame().IsClient())
        {
            m_InLightMode = true;
            m_CurrAnimation = START_ANIM_VALUE;
        }
    }
}```

