    private void RunAthenaDebug()
    {
        Debug.DestroyAllShapes();
        foreach (vector v1: ATHENA_RANDOM_POSITIONS) {
            array<Man> players = {};
            GetGame().GetPlayers(players);
            if (!ZombieBase.CheckPosition(players, v1)) {
                Debug.DrawSphere(v1, 0.25, COLOR_RED);
            } else {
                Debug.DrawSphere(v1, 0.25, COLOR_GREEN);
            }
        }
    }

    private void CheckAthenaSpawn()
    {
        if (DidAthenaSpawn(GetPosition())) {
            vector position = GetAthenaPosition(ATHENA_RANDOM_POSITIONS);
            if (position == vector.Zero) {
                Delete();
                return;
            }

            SetPosition(position);
            Update();
        }
    }

    private static bool DidAthenaSpawn(vector position)
    {        
        foreach (vector possible: ATHENA_SPAWN_POINTS) {
            // Radius is 10
            if (vector.Distance(position, possible) < 25) { 
                return true;
            }
        }

        return false;
    }

    private static vector GetAthenaPosition(TVectorArray possible_positions)
    {
        array<Man> players = {};
        GetGame().GetPlayers(players);

        // Eliminate everyone far away
        for (int i = 0; i < players.Count(); i++) {
            if (vector.Distance(players[i].GetPosition(), possible_positions[0]) > 200) {
                players.Remove(i);
            }
        }

        vector position = possible_positions.GetRandomElement();
        int j;
        while (!CheckPosition(players, position)) {
            position = possible_positions.GetRandomElement();
            j++;
            // just a spooky iteration check
            if (j > 10) { 
                return vector.Zero;
            }
        }

        return position;
    }

    static bool CheckPosition(array<Man> players, vector position)
    {        
        foreach (Man player: players) {
            if (player && vector.Distance(player.GetPosition(), position) < 15) {
                return false;
            }
        }

        return true;
    }
