class GrampawBankAccount : ItemBase
{
    protected float m_Money;
    protected string m_Username;
    protected string m_Password;

    // ran before item is stored in db - used to write variable data to the db
    override void OnStoreSave(ParamsWriteContext ctx)
    {
        super.OnStoreSave(ctx);

        ctx.Write(m_Money);
        ctx.Write(m_Username);
        ctx.Write(m_Password);
    }

    // ran after the item is loaded from db - used to load data to class variables
    override bool OnStoreLoad(ParamsReadContext ctx, int version)
    {
        if (!super.OnStoreLoad(ctx, version))
            return false;

        if (!ctx.Read(m_Money))
            return false;

        if (!ctx.Read(m_Username))
            return false;

        if (!ctx.Read(m_Password))
            return false;

        return true;
    }


    // ran afer OnStoreLoad - used for functional logic
    override void AfterStoreLoad()
    {
        super.AfterStoreLoad();

        if (m_Money < 0)
            Delete();
    }
}
```

