  }
  for (int j = 0; j < 32; j++)
  {
    flagPolePlaced.GetInventory().CreateAttachment("Stone");
  }
  base_building = BaseBuildingBase.Cast( flagPolePlaced );
  construction = base_building.GetConstruction();  
  construction.BuildPartServer( player, "support", AT_BUILD_PART );

  flagPolePlaced.GetInventory().CreateAttachment("MetalWire");
  flagPolePlaced.GetInventory().CreateAttachment("Rope");
  flagPolePlaced.GetInventory().CreateAttachment("Nail");
  for (int k = 0; k < 4; k++)
  {
