override PlayerBase CreateCharacter(PlayerIdentity identity, vector pos, ParamsReadContext ctx, string characterName)
{
    float percentHealth = 0.90;
    float percentBlood = 0.70;

    // better than vanilla; create and assign player entity to identity
    PlayerBase player = PlayerBase.Cast(GetGame().CreatePlayer(identity, characterName, pos, 0, "NONE"));
    GetGame().SelectPlayer(identity, player);

