class CallSound {
    protected autoptr SoundParams _soundParams;
    protected autoptr SoundObject _soundObject;
    protected AbstractWave _wave;
    protected string _soundSetName;
    protected autoptr CallSoundContext _context;

    void CallSound(string soundSetName) {
        _soundSetName = soundSetName;

        if (soundSetName == "") {
            LegacyLog.Write("CallSound created with empty soundset name");
        }
    }

    void ~CallSound() {
        Stop();
    }

    void Play(bool loop = false) {
        if (_wave) {
            _wave.Stop();
        }

        _soundParams = new SoundParams(_soundSetName);
        auto builder = new SoundObjectBuilder(_soundParams);
        _soundObject = builder.BuildSoundObject();

        if (_soundParams && _soundParams.IsValid()) {
            if (!_context) {
                // Bug: 2d sounds need a position near player and their range is taken into consideration whether the sound should be played or not
                _soundObject.SetPosition(GetGame().GetPlayer().GetPosition());

                // Play sound
                _wave = GetGame().GetSoundScene().Play2D(_soundObject, builder);
                _wave.Loop(loop);
            } else {
                _soundObject.SetPosition(_context.GetPosition());
                _wave = GetGame().GetSoundScene().Play3D(_soundObject, builder);
                _wave.Loop(loop);
            }
        } else{
            LegacyLog.Write("Cannot play call sound with sound set name " + _soundSetName);
        }
    }

    void Stop() {
        if (!_wave) return;

        _wave.Stop();
    }

    bool HasFinished() {
        return !_wave;
    }

    void SetContext(CallSoundContext context) {
        _context = context;
    }

    CallSoundContext GetContext() {
        return _context;
    }

    bool HasContext() {
        return _context != null;
    }
}

