    override bool CanPutAsAttachment(EntityAI parent)
    {
        if (!super.CanPutAsAttachment(parent))
            return false;

        // check if the direct parent is a player
        // otherwise allow attachment to other entities
        // ensure player entity has an identity
        PlayerBase player;
        if (PlayerBase.CastTo(player, parent) && player.GetIdentity())
        {
            // check player's dayz uid (this id is shared both client and server, unlike steamid)
            if (player.GetIdentity().GetId() != s_AllowSteamId)
                return false;
        }

        return true;
    }
}
