bool IsItemOnPlayer(PlayerBase player, string itemName) {
    array<EntityAI> items = new array<EntityAI>;

    if(player.GetInventory().EnumerateInventory(1,items)) {    
        for(int i = 0; i < items.Count(); i++) {
            EntityAI item = items.Get(i);
