// probably don't want to use BuildingBase for this case, as all buildings would now contain this logic
// use the object or a base type of the objects you're wanting to handle this on
modded class BuildingBase
{
    protected ref Timer m_CloseTimer;

    void BuildingBase()
    {
        m_CloseTimer = new Timer(CALL_CATEGORY_SYSTEM); // timer will always run no matter game state
    }

    void AutoCloseDoor(int index)
    {
        // close door after 10 seconds via timer
        m_CloseTimer.Run(10, this, "CloseDoor", index);
    }
}

// this will be in your action
override void OnStartServer(ActionData action_data)
{
    BuildingBase building; // cast to our type object
    if (!BuildingBase.CastTo(building, action_data.m_Target.GetObject()))
        return;

    int doorIndex = building.GetDoorIndex(action_data.m_Target.GetComponentIndex());
    if (doorIndex != -1)
    {
        building.OpenDoor(doorIndex);
        building.AutoCloseDoor(doorIndex);
    }
}
