{
    protected string m_text;
    ref KPConditionBase m_condition;

    void SetCondition(string cond)
    {
        m_condition = KPConditionFactory.GetCondition(cond);

    }

    bool IsSatisfied()
    {
        return m_condition.SatisfiesCondition();
    }

