class ExternalServerName
{
    static const string IP_BASE = "https://ip4.seeip.org/";
    static const string DZSAL_BASE = "https://dayzsalauncher.com/api/v1/";

    static bool FetchFromQuery(out string serverName)
    {
        RestContext rest = GetRestApi().GetRestContext(IP_BASE);
        string serverIp = rest.GET_now(string.Empty);

        int queryPort = GetGame().ServerConfigGetInt("steamQueryPort");
        if (queryPort < 1) queryPort = 27016;

        string request = string.Format("query/%1/%2", serverIp, queryPort);

        rest = GetRestApi().GetRestContext(DZSAL_BASE);
        string json = rest.GET_now(request);

        JsonSerializer js = new JsonSerializer();
        DZSALResponse response = new DZSALResponse();

        string error;
        if (!js.ReadFromString(response, json, error))
            return false;

        if (response.status != 0)
            return false;

        if (!response.result)
            return false;

        serverName = response.result.name;
        return true;
    }
}
