class ActionFillWaterBottle: ActionSingle
{
    void ActionFillWaterBottle()
    {
        // Set the display name of the action
        m_CommandUID = DayZPlayerConstants.CMD_ACTIONMOD_FILLWATERBOTTLE;
        m_StanceMask = DayZPlayerConstants.STANCEMASK_CROUCH | DayZPlayerConstants.STANCEMASK_ERECT;
        m_SpecialtyWeight = UASoftSkillsWeight.PRECISE_HIGH;
        m_FullBody = true;
        m_SyncToClient = true;
    }

    override void CreateConditionComponents()  
    {
        m_ConditionItem = new CCINonRuined;
        m_ConditionTarget = new CCTRefillable(UAMaxDistances.DEFAULT);
    }

    override int GetType()
    {
        return AT_FILL_WATER_BOTTLE;
    }

    override string GetText()
    {
        return "Fill Water Bottle";
    }

    override bool ActionCondition( PlayerBase player, ActionTarget target, ItemBase item )
    {
        // Check if player has an empty water bottle in hand
        if ( item.Type() == ATTACHMENT_WATER_BOTTLE && item.GetQuantity() == 0 )
        {
            return true;
        }
        return false;
    }

    override void OnCompleteServer( PlayerBase player, ActionTarget target, ItemBase item, Param acdata )
    {
        // Fill the water bottle with water from the tank
        float waterAmount = item.GetVarMaxAmount("m_liquidType", item.GetQuantityMax());
        item.AddQuantity(waterAmount, false, false);
    }
};


