class FlameShirt extends M65Jacket_Black
{
    private const int INITIAL_DELAY = 100;
    private const int ANIMATION_MS = 8000;
    private const int IMAGE_COUNT = 31;

    private ref array<string> imgPaths;
    private int idx;
    private int inc;

    void FlameShirt()
    {
        if (!GetGame().IsDedicatedServer())
            GetGame().GetCallQueue(CALL_CATEGORY_GAMEPLAY).CallLater(WeaponInit, INITIAL_DELAY, false);
    }

    void ~FlameShirt()
    {
        if (GetGame() && !GetGame().IsDedicatedServer())
            GetGame().GetCallQueue(CALL_CATEGORY_GAMEPLAY).Remove(UpdateCamos);
    }

    void WeaponInit()
    {
        imgPaths = {};
        idx = 0;
        inc = 1;

        for (int i = 1; i < IMAGE_COUNT; i++)
            imgPaths.Insert(string.Format("ShirtTest\\Data\\ClothingTextures\\AnimatedShirts\\Flame (%1).paa", i));

        int delayMs = ANIMATION_MS / (IMAGE_COUNT * 2);
        GetGame().GetCallQueue(CALL_CATEGORY_GAMEPLAY).CallLater(UpdateCamos, delayMs, true);
    }

    void UpdateCamos()
    {
        SetObjectTexture(0, imgPaths[idx]);
        SetObjectTexture(1, imgPaths[idx]);
        SetObjectTexture(2, imgPaths[idx]);

        if (idx + inc < 0 || idx + inc == IMAGE_COUNT)
            inc *= -1;

        idx += inc;
    }
}
