modded class MissionGameplay
{
    ref CustomMod m_CustomMod;

    override void OnMissionStart()
    {
        super.OnMissionStart();
        Print("[CustomMod] Mission init ...");
        if ( GetGame().IsClient() )
            Print("[CustomMod] Mission init for client!");
            m_CustomMod = new ref CustomMod();
    }

    override void OnMissionFinish()
    {
        super.OnMissionFinish();
        if ( m_CustomMod )
            delete m_CustomMod;
    }

    override void OnKeyPress(int key)
    {
        super.OnKeyPress(key);
        Print("[CustomMod] Key Pressed!!!");
        if ( !isAnotherMenuOpen() && key == KeyCode.KC_P )
        {
            if ( !m_CustomMod.getWWindow() )
                m_CustomMod.setWWindow();
            m_CustomMod.getWWindow().OpenMenu();
        }
    }

    bool isAnotherMenuOpen()
    {
        for ( int i_menu = 1; i_menu < 31; i_menu++ )
        {
            if ( GetGame().GetUIManager().IsMenuOpen( i_menu ) )
                return true;
        }
        return false;
    }

};
