class ObjectManager<T> : Managed
{
    private ref array<T> m_Objects;

    void ObjectManager()
    {
        m_Objects = new array<T>();
    }

    void ~ObjectManager()
    {
        m_Objects.Clear();
    }

    T CreateObject(string objectTypeName, vector position = "0 0 0", bool is_networked = false, bool initPhysics = false)
    {
        T obj = T.Cast(GetGame().CreateObject(objectTypeName, position, is_networked, initPhysics));
        if (obj)
        {
            m_Objects.Insert(obj);
        }
        return obj;
    }

    void RemoveObject(T obj)
    {
        int index = m_Objects.Find(obj);
        if (index >= 0)
        {
            m_Objects.Remove(index);
            GetGame().ObjectDelete(obj);
        }
    }

    int GetObjectCount()
    {
        return m_Objects.Count();
    }

    T GetObjectAtIndex(int index)
    {
        if (index >= 0 && index < m_Objects.Count())
        {
            return m_Objects[index];
        }
        return null;
    }
