bool IsCollidingWithFenceSphere()
{
    float radius = 1.0;
    vector checkPosition = m_Projection.GetPosition();
    array<Object> nearbyObjects = new array<Object>();
    array<CargoBase> proxyCargos = new array<CargoBase>();

    GetGame().GetObjectsAtPosition3D(checkPosition, radius, nearbyObjects, proxyCargos);
    for (int i = 0; i < nearbyObjects.Count(); i++)
    {
        Object obj = nearbyObjects.Get(i);

        if (obj.IsKindOf("Chew_FenceKit_Projection"))
        {
            for (int j = 0; j < nearbyObjects.Count(); j++)
            {
                Object fence = nearbyObjects.Get(j);
                if (fence.IsKindOf("Fence"))
                {
                    return true;
                }
            }
        }
    }
    return false;
}
