```
class Queue<Class T>
{
    ref array<T> m_Data;

    void Queue()
    {
        m_Data = new array<T>;
    }

    void Enqueue(T item)
    {
        m_Data.Insert(item);
    }

    T Dequeue()
    {
        if (m_Data.Count() > 0)
        {
            T item = m_Data.Get(0);
            m_Data.Remove(0);
            return item;
        }
        return 0;
    }

    int Count()
    {
        return m_Data.Count();
    }

    bool IsEmpty()
    {
        return m_Data.Count() == 0;
    }
}
