Man GetPlayerObjectFromIdentity(PlayerIdentity indentity)
{
    if(!indentity && !GetGame().IsMultiplayer()) return GetGame().GetPlayer();

    array<Man> players = new array<Man>;
    GetGame().GetWorld().GetPlayerList(players);

    string playerID = indentity.GetId();
    for ( int i = 0; i < players.Count(); ++i ) 
    {
        Man player = players[i];
        PlayerIdentity p_identity = player.GetIdentity();

        if( p_identity && p_identity.GetId() == playerID)
        {
            return player;
        };
    };
    return NULL;
};
