class multideminsionArray{
    private ref array<ref array<int>> rows;

    void multideminsionArray(int deminsions){
        rows = new array<ref array<int>>;

        for(int i = 0; i < deminsions; i++){
            rows.Insert(new array<int>);
        }
    }

    int Count(){
        int count = 0;
        foreach(array<int> a : rows){
            foreach(int x : a){
                count++;
            }
        }
    }

    void Clear(){
        foreach(array<int> b : rows){
            b.Clear();
        }
    }

    void InsertAt(int row, int column, int value){
        rows.Get(row).InsertAt(value, column);
    }

    void RemoveAt(int row, int col){
        rows.Get(row).Remove(col); 
    }
    int Get(int row, int col){
       return rows.Get(row).Get(col);
    } 
}
