class Queue<Class T>{
    private ref array<T> queue;

    void Queue<T>(){
        queue = new array<T>;
    }

    int Count(){
        return queue.Count();
    }

    T getQueuedObject(){
        return queue.Get(0);
    }

    bool hasNextQueue(){
        return queue.Get(0) != NULL;
    }

    void next(){
        for(int i = 1; i < queue.Count(); i++){
            queue.Set(i - 1, queue.Get(i));
        }
    }
}
