class FliesBuzzing
{
    ref SoundObject soundObj; //Sound for flies
    ref SoundObjectBuilder soundBuilder;
    AbstractWave FliesSounds;

    private bool m_soundStatus = true;

    AbstractWave MSaySoundSet(string name)
    {
        SoundParams soundParams = new SoundParams(name);
        soundBuilder            = new SoundObjectBuilder(soundParams);
        soundObj                = soundBuilder.BuildSoundObject();
        soundObj.SetPosition(GetPosition());
        return GetGame().GetSoundScene().Play3D(soundObj, soundBuilder);
    }

    void FliesBuzzing()
    {
        if (!GetGame().IsServer() && m_soundStatus)
        {
            FliesSounds = MSaySoundSet("Fliesbuzzing_SoundSet"); //Hook sound to body
            FliesSounds.Loop(true);
            soundObj.SetPosition(GetPosition());
        }
    }

    void ~FliesBuzzing()
    {
        if ( FliesSounds )
        {
            FliesSounds.Stop();
        }
    }
}
