```c
void PartyMeMenuHandler()
{
    if ( !g_Game.GetUIManager().IsMenuOpen( MENU_CHAT ) && !g_Game.GetUIManager().IsMenuOpen( PARTYME_MENU ) && g_Game.GetUIManager().GetMenu() == NULL )
    {
        g_Game.GetUIManager().EnterScriptedMenu(PARTYME_MENU, NULL);
        g_Game.GetMission().PlayerControlDisable(INPUT_EXCLUDE_INVENTORY);
        g_Game.GetUIManager().ShowUICursor(true);
        g_Game.GetUIManager().ShowCursor(true);
        g_Game.GetInput().ChangeGameFocus( 1 );
        g_Game.GetMission().GetHud().Show( false );
    }
    else if ( !g_Game.GetUIManager().IsMenuOpen( MENU_CHAT ) && g_Game.GetUIManager().IsMenuOpen( PARTYME_MENU ) )
    {
        closePartyMeMenu();
    }
   }

void closePartyMeMenu()
{
    g_Game.GetUIManager().ShowCursor(false);
    g_Game.GetUIManager().ShowUICursor(false);
    g_Game.GetInput().ResetGameFocus();
    g_Game.GetMission().PlayerControlEnable();
    g_Game.GetUIManager().Back();
    g_Game.GetMission().GetHud().Show( true );
    return;
}
```

