```C
class LimitedArray<Class T>
{
    protected int m_maxSizeArray;
    ref array<T> m_array;

    void LimitedArray(int maxSizeArray)
    {
        m_maxSizeArray = maxSizeArray;
        m_array = new array<T>;
    }

    int Insert(T elementToAdd)
    {
        if ( m_array.Count() == m_maxSizeArray )
              m_array.Remove(0);
        return m_array.Insert(elementToAdd);
    }

    int Count()
    {
        return m_array.Count();
    }

    // Return the maximum of items array can contain
    int Limit()
    {
        return m_maxSizeArray;
    }
}
```
