class UniqueItem extends ItemBase
{
    private int _i1, _i2, _i3, _i4;
    protected int m_UniqueID = 0;

    void UniqueItem()
    {
        RegisterNetSyncVariableInt("m_UniqueID");
    }

    override string GetDisplayName()
    {
        if (m_UniqueID != 0)
        {
            return m_UniqueID.ToString();
        }

        return ConfigGetString("displayName");
    }

    override void OnStoreSave(ParamsWriteContext ctx)
    {
        super.OnStoreSave(ctx);
        ctx.Write(m_UniqueID);
    }

    override bool OnStoreLoad(ParamsReadContext ctx, int version)
    {
        if (!super.OnStoreLoad(ctx, version))
            return false;

        if (!ctx.Read(m_UniqueID))
            return false;

        return true;
    }

    override void EOnInit(IEntity other, int extra)
    {
        super.EOnInit(other, extra);

        GetPersistentID(_i1, _i2, _i3, _i4);
        m_UniqueID = _i1 + _i2 + _i3 + _i4;
        SetSynchDirty();
    }

    int GetUniqueID()
    {
        return m_UniqueID;
    }
}
