static int StringToEnum(typename e, string enumName)
{
    int count = e.GetVariableCount();
    int value;

    for (int i = 0; i < count; i++)
    {
        if (e.GetVariableType(i) == int && e.GetVariableValue(null, i, value) && e.GetVariableName(i) == enumName)
