```cs
modded class Construction
{
    override bool IsColliding(string part_name)
    {
        // forces garden plots to have vanilla collision detection
        if (!GetParent().IsInherited(GardenPlot))
            return false;

        return super.IsColliding(part_name);
    }
}

modded class Hologram
{
    override void EvaluateCollision()
    {
        // allows objects to be placed anywhere except garden plots
        if (!m_Projection.IsInherited(GardenPlot))
        {
            SetIsColliding(false);
            return;
        }

        super.EvaluateCollision();
    }
}
