override void EEHitBy(TotalDamageResult damageResult, int damageType, EntityAI source, int component, string dmgZone, string ammo, vector modelPos)
{
  super.EEHitBy(damageResult, damageType, source, component, dmgZone, ammo, modelPos);
  PlayerBase pb_source = PlayerBase.Cast(source);
  if (pb_source) {
    logDeath("Player '"+this.m_Name+"' (uid="+this.m_Id+") has been hit by player '"+pb_source.m_Name+"'(uid="+pb_source.m_Id+") to "+dmgZone+" with "+ammo);
  } else {
    logDeath("Player '"+this.m_Name+"' (uid="+this.m_Id+") has been hit by '"+source.GetDisplayName()+"' to "+dmgZone+" with "+ammo);
  }
}
