class AeryesAlgorithms
{

    ref TIntArray algoarray;

    ///////////
    //Sorting//
    ///////////

    // Bubble Sort
    void BubbleSort(int n, TIntArray array)
    {
        int temp;
        int temptwo;
        algoarray = new TIntArray; 
        algoarray = array;

        for(int i = 0; i < n-1; i++)
        {
            for(int j = 0; j < n-i-1; j++)
            {
                if(array.Get(j) > algoarray.Get(i))
                {
                    temp = algoarray.Get(j);
                    temptwo = algoarray.Get(i);

                    algoarray.Remove(j);
                    algoarray.Remove(i);

                    algoarray.InsertAt(temp, i);
                    algoarray.InsertAt(temptwo, j);
                };
            };        
        }; 

        // Print the sorted array contents.
        for(int k; k < algoarray.Count(); k++)
        {
            Print(k + "element is: " + algoarray.Get(k));
        };
    };
};```

