array<Object> GetElements(initialArray, numberOfElementsToGet)
{
  ref array<Object> randomElements = new array<Object>;
  ref array<Object> copyInitialArray = new array<Object>;
  int randomIndex = -1;

  copyInitialArray.Copy(initialArray);
  // Instead of "copyInitialArray.Count()" you could create an int before the loop, and decrementing at same time as numberOfElementsToGet
  // It would prevent infinite loop if array is not big enough.
  while (numberOfElementsToGet > -1 && copyInitialArray.Count())
  {
    randomIndex = copyInitialArray.GetRandomIndex();
    randomElements.Insert(copyInitialArray[randomIndex]);
    copyInitialArray.Remove(randomIndex);
    numberOfElementsToGet--;
  }
  return randomElements;
}
