void Pseudo()
{
    ItemBase itemHands = ItemBase.Cast(player.GetItemInHands());

    if (itemHands)
    {
        // if item is inherits SpecialItem or is SpecialItem
        if (itemHands.IsInherited(SpecialItem))
        {
            itemHands.Delete();
        }
        else
        {
            // drops the item correctly and syncs
            if (!GetGame().IsMultiplayer())
                player.PredictiveDropEntity(this);
            else
                player.ServerDropEntity(this);

            return; // doesn't continue
        }

        ProceedToFunction(player);
    }
}
