```cs
void Pseudo()
{
    ItemBase itemHands = ItemBase.Cast(player.GetItemInHands());
    ItemBase newItem = ItemBase.Cast(GetGame().CreateObject("SpecialItem", player.GetPosition()));

    if (itemHands)
    {
        if (GetGame().IsMultiplayer())
            player.ServerDropEntity(itemHands);
        else
            player.LocalDropEntity(itemHands);
    }

    if (newItem)
    {
        if (GetGame().IsMultiplayer())
            player.ServerTakeEntityToHands(newItem);
        else
            player.LocalTakeEntityToHands(newItem);

        ProceedToFunction(player);
    }
}
