class LightBeam2 extends SpotLightBase
{
    float m_pos2 = 0;
    float m_pos3 = 0;

        void LightBeam2()
    {
        SetVisibleDuringDaylight( true );
        SetRadius( 300 );
        SetCastShadow( true );    
        SetDiffuseColor( 0.5, 0.5, 0.8 );
        SetAmbientColor( 0.5, 0.5, 0.8 );
        SetAmbientAlpha( 1 );
        SetEnabled( true );
        SetFlareVisible( true );
        SetSpotLightAngle( 5 );
    }

    override void OnFrameLightSource(IEntity other, float timeSlice)
    {
        super.OnFrameLightSource( other, timeSlice);


        if (m_pos2 < 360)
        {
            m_pos2 += 12 * timeSlice;
            m_pos3 += 0.1 * timeSlice;
        }
        else
        {
            m_pos2 = 0;
            m_pos3 = 0;
        }    
        this.SetOrientation(Vector(m_pos2, 0, 0));
        this.SetPosition(Vector(0, m_pos3, 0));
    }
}```

