```    bool CheckRadioSignal (PlayerBase player)
    {
        TR_FREQ = player.GetRadioBeaconFreq();
        ref array<EntityAI> PlayerItems = new array<EntityAI>;
        if (player.GetInventory().EnumerateInventory( InventoryTraversalType.PREORDER, PlayerItems) )
        {
            for (int i=0; i < PlayerItems.Count(); i++)
            {
                EntityAI entity_item = PlayerItems.Get(i);
                ItemBase item;
                Class.CastTo(item, entity_item);

                if (item && item.IsTransmitter() )
                {
                    ItemTransmitter Transmitter;
                    InventoryItem inv_item;

                    Class.CastTo(Transmitter, item);
                    Class.CastTo(inv_item, entity_item);

                    if (inv_item.IsOn() && Transmitter.IsReceiving() && Transmitter.GetTunedFrequency() == TR_FREQ ) return true;
                }
            }
            return false;
        }
        else return true;
    }```

