typedef map<string, vector> TBaseLocations;
typedef map<string, int> TRespawnTimers;

class BaseRespawnModule: JMModuleBase
{    
    static const int BASE_RESPAWN_TIMER = 3600; // 1hr

    protected ref Timer m_ModuleSaveTimer;
    protected ref TBaseLocations m_BaseLocations;
    protected ref TStringArray m_BaseRespawns;
    protected ref TRespawnTimers m_RespawnTimers;

    void BaseRespawnModule()
    {
        m_BaseLocations = new TBaseLocations();
        m_RespawnTimers = new TRespawnTimers();
        m_BaseRespawns = {};
    }

    void ~BaseRespawnModule()
    {
        delete m_BaseLocations;
        delete m_BaseRespawns;
        delete m_RespawnTimers;
    }

    override void Init()
    {
        m_ModuleSaveTimer = new Timer(CALL_CATEGORY_SYSTEM);
        m_ModuleSaveTimer.Run(20.0, this, "OnModuleSave", null, true);

        GetRPCManager().AddRPC("BaseRespawnModule", "SetRespawnBase", this);
    }

    void RegisterBaseLocation(PlayerBase player, vector location)
    {
        PrintFormat("Assigning base location for %1", player.GetIdentity().GetName());
        m_BaseLocations.Insert(String(player.GetIdentity().GetId()), location);
        player.SetBaseAssigned(true);
    }

    void RemoveBaseLocation(PlayerBase player)
    {
        PrintFormat("Removing base location for %1", player.GetIdentity().GetName());
        m_BaseLocations.Remove(String(player.GetIdentity().GetId()));
        player.SetBaseAssigned(false);
    }

    vector GetBaseLocation(PlayerBase player)
    {    
        return m_BaseLocations[String(player.GetIdentity().GetId())];
    }

    void SetRespawnBase(CallType type, ref ParamsReadContext ctx, ref PlayerIdentity sender, ref Object target)
    {
        PrintFormat("%1 requested to be respawned at base", sender.GetName());
        m_BaseRespawns.Insert(sender.GetId());
    }

    override void OnInvokeConnect(PlayerBase player, PlayerIdentity identity)
    {    
        player.SetBaseAssigned(m_BaseLocations[identity.GetId()] != vector.Zero);

        int idx = m_BaseRespawns.Find(identity.GetId());
        if (idx != -1) {
            PrintFormat("Attempting to spawn %1 at their base", identity.GetName());
            m_BaseRespawns.Remove(idx);
            if (m_BaseLocations[identity.GetId()] != vector.Zero) {
                player.SetPosition(m_BaseLocations[identity.GetId()]);
                player.Update();
                player.SetBaseRespawnTimer(BASE_RESPAWN_TIMER);
                return;
            }
        }

        player.SetBaseRespawnTimer(m_RespawnTimers[identity.GetId()]);
    }

    override void OnClientRespawn(PlayerBase player, PlayerIdentity identity)
    {
        m_RespawnTimers[player.GetIdentity().GetId()] = player.GetBaseRespawnTime();
    }

    //! Just serializing as json for now, to easily modify if needed
    void OnModuleSave()
    {
        JsonFileLoader<TBaseLocations>.JsonSaveFile("$profile:\\DataCache\\base_locations.json", m_BaseLocations);
    }

    override void OnMissionStart()
    {
        JsonFileLoader<TBaseLocations>.JsonLoadFile("$profile:\\DataCache\\base_locations.json", m_BaseLocations);
    }

    override void OnMissionFinish()
    {
        JsonFileLoader<TBaseLocations>.JsonSaveFile("$profile:\\DataCache\\base_locations.json", m_BaseLocations);
    }

    override bool IsServer()
    {
        return true;
    }

    override bool IsClient()
    {
        return false;
    }
}```

