    // Function to send agents rpc to client to "sync" the variables
    void SyncRageAgentsFromServerToClient()
    {
        if ( !GetGame().IsServer() )    return;

        auto agents = new Param1<int>( GetSingleAgentCount( ModdedAgents.ModdedAgent ) );

        GetGame().RPCSingleParam( this, ModdedRPCs.RPC_SYNC_AGENTS, agents, true, GetIdentity() );
    }

    // Function that executes when an rpc is recieved
    override void OnRPC( PlayerIdentity sender, int rpc_type, ParamsReadContext ctx )
    {
        super.OnRPC( sender, rpc_type, ctx );

        if ( !GetGame().IsClient() )    return;

        // When the client recieves the server's agents rpc, set the clientside agents variable
        if ( rpc_type == ModdedRPCs.RPC_SYNC_AGENTS )
        {
            Param1<int> paramClientAgents;

            if ( !ctx.Read( paramClientAgents ) )    return;

            clientAgents = paramClientAgents.param1;
        }
    }
